attachments.value:
{%raw%}
{%- set content = "" -%}
{%- if output | length > 0 -%}
    {%- set raw_content = output[0].get('message') -%}
    {%- if raw_content -%}
        {# Check if "html" text is present in the content #}
        {%- if "html" in raw_content -%}
            {# Find the position after "html" and extract content from there #}
            {%- set html_pos = raw_content.find('html') -%}
            {%- set content_start = html_pos + 4 -%}
            {%- set content = raw_content[content_start:] | trim -%}
        {%- else -%}
            {# If no "html" text found, use the content as is #}
            {%- set content = raw_content -%}
        {%- endif -%}
    {%- else -%}
        {%- set content = "Evaluation Report has not been generated for this Conversation." -%}
    {%- endif -%}
{%- else -%}
    {%- set content = "Evaluation Report has not been generated for this Conversation." -%}
{%- endif -%}
<div style='overflow:auto;width:100%;height:100%;'> {{ content }} </div>
{%endraw%}
