{% set ns = namespace() %}
{% set query = "project_id is '" + projectid + "' and incident_id is '" + incidentid + "' " %}
{% set incidentstream = "oia-incidents-stream" %}
{% set incident_res = engine.query_stream_data(stream=incidentstream, cfxql_query=query) %}
{% if incident_res is defined and incident_res|length > 0 %}
    {% set incident = incident_res[0] %}
    {% set config_query = "roomid is '" + projectid + "' and pagename is 'topology' " %}
    {% set config_stream = "oia-incident-page-config" %}
    {% set config_res = engine.query_stream_data(stream=config_stream, cfxql_query=config_query) %}
    {% set page_config = {} %}
    {% if config_res is defined and config_res|length > 0 %}
        {% set config_json_res = config_res[0] %}
        {% set config_json = config_json_res.get("config_json") %}
        {% set config_json_type = "string" %}
        {% if config_json is mapping and config_json is not string %}
            {% set config_json_type = "mapping" %}
        {% elif config_json is sequence and config_json is not mapping and config_json is not string %}
            {% set config_json_type = "list" %}
        {% endif %}
        {% if config_json and config_json_type == "string" %}
            {% set page_config = json_loads(config_json) %}
        {% endif %}
    {% endif %}

    ## defaults for topology - start
    {% set node_id_col_incidents = "attrs_node_id" %}
    {% set node_id_col_alerts = "a_en_node_id" %}
    {% set related_alerts_enabled = True %}
    {% set related_nodes_max_distance = 2 %}
    {% set stack_details_type = "use_context" %}
    {% set context_field_for_stack_name = "attrs_stack_name" %}
    {% set fixed_stack_name = "" %}
    {% set stack_definition = {} %}
    ## defaults for topology - end

    {% set topology_widget = dashboard["dashboard_sections"][0]["widgets"][0] %}
    {% if topology_widget %}
        {% if page_config %}
            {% set _ = logger.debug("page_config is: " + str(page_config)) %}

            {% set node_id_col_incidents = page_config.get("node_id_col_incidents_stream", node_id_col_incidents) %}
            {% set node_id_col_alerts = page_config.get("node_id_col_alerts_stream", node_id_col_alerts) %}

            {% set _ = topology_widget.__setitem__("node_id_attribute", node_id_col_incidents) %}
            {% set _ = topology_widget["oia_details"]["alert_source"].__setitem__("group_by", [node_id_col_alerts] + topology_widget["oia_details"]["alert_source"]["group_by"]) %}

            {% set related_alerts = page_config.get("related_alerts") %}
            {% if related_alerts%}
                {% set related_alerts_enabled = related_alerts.get("enabled", related_alerts_enabled) %}
                {% set related_nodes_max_distance = related_alerts.get("related_nodes_max_distance", related_nodes_max_distance) %}
            {% else %}
                {% set related_alerts_enabled = False %}
            {% endif %}
            {% if related_alerts_enabled == True or ( related_alerts_enabled is string and related_alerts_enabled|lower in ("yes", "true") ) %}
                {% set _ = topology_widget.__setitem__("show_alert_details", true) %}
                {% set _ = topology_widget["oia_details"]["alert_source"].__setitem__("related_nodes_max_distance", related_nodes_max_distance) %}
            {% else %}
                {% set _ = topology_widget.__setitem__("show_alert_details", false) %}
            {% endif %}

            {% set stack_details = page_config.get("stack_details") %}
            {% if stack_details %}
                {% set stack_details_type = stack_details.get("type", stack_details_type) %}

                {% if stack_details_type == "use_context" %}
                    {% set context_field_for_stack_name = stack_details.get("context_field_for_stack_name", context_field_for_stack_name) %}
                    {% set _ = topology_widget["oia_details"].__setitem__("stack_name_attribute", context_field_for_stack_name) %}
                {% elif stack_details_type == "fixed" %}
                    {% set fixed_stack_name = stack_details.get("stack_name") %}
                    {% set _ = topology_widget["oia_details"].__setitem__("stack", fixed_stack_name) %}
                    {% set _ = topology_widget.__setitem__("stack_name", fixed_stack_name) %}
                {% elif stack_details_type == "dynamic" %}
                    {% set stack_definition = stack_details.get("stack_definition") %}
                    {% set _ = topology_widget["oia_details"].__setitem__("stack_definition", stack_definition) %}
                {% endif %}
            {% endif %}
        {% else %}
            {% set _ = logger.info("No page_config defined for topology hence using defaults.") %}

            {% set _ = topology_widget.__setitem__("node_id_attribute", node_id_col_incidents) %}
            {% set _ = topology_widget["oia_details"]["alert_source"].__setitem__("group_by", [node_id_col_alerts] + topology_widget["oia_details"]["alert_source"]["group_by"]) %}

            {% set _ = topology_widget.__setitem__("show_alert_details", true) %}
            {% set _ = topology_widget["oia_details"]["alert_source"].__setitem__("related_nodes_max_distance", related_nodes_max_distance) %}
            {% set _ = topology_widget["oia_details"].__setitem__("stack_name_attribute", context_field_for_stack_name) %}
        {% endif %}
    {% else %}
        {% set _ = logger.info("No topology widget defined in dashboard.") %}
    {% endif %}
{% else %}
  {% set _ = logger.warning("Can not find incident details hence can not show topology for incident: " + incidentid) %}
{% endif %}
{% set _ = logger.debug("Final topology dashboard: " + str(dashboard)) %}
