{%- set existing_groups = dashboard["dashboard_groups"] -%}
{%- set existing_pages = dashboard["dashboard_pages"] | map(attribute='name') | list -%}

{%- set d_groups = {} -%}
{%- set d_pages_by_group = {} -%}
{%- set filtered_dlist = [] -%}
{%- set highest_version_by_pack = {} -%}

{%- set dlist = engine.query_stream_data(
    stream="rda_packs_dynamic_groups_and_pages",
    cfxql_query="customer_tag is empty and dashboard_group_label is not 'Multi Network Discovery'"
) or [] -%}

{# ---------------- Semantic version filtering ---------------- #}
{%- for row in dlist -%}
    {%- set pid = row.pack_id|string -%}
    {%- set ver = row.pack_version|string -%}
    {%- set ver_parts = ver.split('.') | map('int') | list -%}

    {%- if pid not in highest_version_by_pack -%}
        {%- set _ = highest_version_by_pack.update({pid: ver_parts}) -%}
        {%- set _ = filtered_dlist.append(row) -%}
    {%- else -%}
        {%- set existing_ver_parts = highest_version_by_pack[pid] -%}
        {%- set is_higher = false -%}

        {%- if ver_parts[0] > existing_ver_parts[0] -%}
            {%- set is_higher = true -%}
        {%- elif ver_parts[0] == existing_ver_parts[0] -%}
            {%- if ver_parts[1] > existing_ver_parts[1] -%}
                {%- set is_higher = true -%}
            {%- elif ver_parts[1] == existing_ver_parts[1] -%}
                {%- if ver_parts[2] > existing_ver_parts[2] -%}
                    {%- set is_higher = true -%}
                {%- endif -%}
            {%- endif -%}
        {%- endif -%}

        {%- if is_higher -%}
            {%- set _ = highest_version_by_pack.update({pid: ver_parts}) -%}
            {%- set _ = filtered_dlist.clear() -%}
            {%- set _ = filtered_dlist.append(row) -%}
        {%- elif ver_parts == existing_ver_parts -%}
            {%- set _ = filtered_dlist.append(row) -%}
        {%- endif -%}
    {%- endif -%}
{%- endfor -%}

{# ---------------- Build groups and pages ---------------- #}
{%- for i in filtered_dlist -%}
    {%- set page_name = i.dashboard_page_name -%}
    {%- if "device_syslog_from_alert_fault" not in page_name
          and "device_SNMP_Traps_from_alerts_fault" not in page_name
          and "device_incidents_from_alerts" not in page_name
          and "device_alerts_from_alert_fault" not in page_name
          and not page_name.startswith("multi") -%}

        {%- set gname = i.dashboard_group_name -%}
        {%- if gname not in d_groups -%}
            {%- set _ = d_groups.update({
                gname: {"icon": i.dashboard_group_icon, "label": i.dashboard_group_label}
            }) -%}
        {%- endif -%}

        {%- set page = {
            "name": i.dashboard_page_name,
            "label": i.dashboard_page_label,
            "icon": i.dashboard_page_icon,
            "group": gname
        } -%}

        {%- if gname not in d_pages_by_group -%}
            {%- set _ = d_pages_by_group.update({gname: [page]}) -%}
        {%- else -%}
            {%- set existing_names = d_pages_by_group[gname] | map(attribute="name") | list -%}
            {%- if page.name not in existing_names -%}
                {%- set _ = d_pages_by_group[gname].append(page) -%}
            {%- endif -%}
        {%- endif -%}

    {%- endif -%}
{%- endfor -%}

{# ---------------- Merge groups ---------------- #}
{%- for gname, gdata in d_groups.items() -%}
    {%- set existing_group_names = existing_groups | map(attribute='g_name') | list -%}
    {%- if gname not in existing_group_names -%}
        {%- set _ = dashboard["dashboard_groups"].update({gname: {"icon": gdata.icon, "label": gdata.label}}) -%}
    {%- endif -%}
{%- endfor -%}

{# ---------------- Merge pages ---------------- #}
{%- for gname, plist in d_pages_by_group.items() -%}
    {%- for page in plist -%}
        {%- if page.name not in existing_pages -%}
            {%- set _ = dashboard["dashboard_pages"].append(page) -%}
        {%- endif -%}
    {%- endfor -%}
{%- endfor -%}
