{%- set widgets =  dashboard["dashboard_sections"][0]["widgets"] -%}
{%- set new_widgets =  [] -%}
{%- set ns =  namespace(count=0) -%}

{%- for w in widgets -%}
    {%- if w.get("widget_type") -%}
        {%-
            set nw = {
                "title": "JSON",
                "widget_group": "g"+str(ns.count),
                "min_width": w.get("min_width", 6),
                "max_width": w.get("max_width", 6),
                "height": w.get("height", 4),
                "widget_type": "codeblock",
                "content": json_dumps_indent(w, 2),
                "lang": "json",
                "widget_id": w.get("widget_id", str(ns.count)) + "_code"
            }
        -%}
        {%- set _ = w.update({"widget_group":  "g"+str(ns.count) }) -%}
        {%- set _ = new_widgets.append(nw) -%}
        {%- set ns.count = ns.count + 1 -%}
    {%- endif -%}
{%- endfor -%}

{%- set _ = widgets.extend(new_widgets) -%}
